import React, { useState, useEffect } from 'react';
import './css/home'

// Images
import sliderBg from '../image/slider_bg.jpg'; // Adjusted path as per common practice

import snowpark from '../image/snowpark-home-slide-decoration-bottomm.png';
import star from '../image/5star.png';


import aws from '../image/scope-india-aws-certification-course-(1).jpg';
import cloud from '../image/scope-india-azure-certification-course.jpg';
import digitalmarketing from '../image/scope-india-digital-marketing-course.jpg';
import dotnet from '../image/scope-india-dotnet-core-full-stack-course.jpg';
import flutter from '../image/scope-india-flutter-fullstack-course.jpg';

import java from '../image/scope-india-java-full-stack-course (1).jpg';
import mern from '../image/scope-india-mean-mern-stack-course.jpg';
import network from '../image/scope-india-network-server-administration-course (1).jpg';
import php from '../image/scope-india-php-full-stack-course (1).jpg';
import python from '../image/scope-india-python-full-stack-course (1).jpg';
import software from '../image/scope-india-software-testing-manual-automation-course.jpg';
import software2 from '../image/scope-india-software-testing-manual-automation-course.jpg';

// Components
// import ContactForm from './css/contactform.css';
// import Homenavigate from './css/homenavigate.css';
// import Footer from './css/footer.css';

const Home = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const images = [ aws, cloud, digitalmarketing, dotnet, flutter,  java, mern, network, php, python, software, software2];

  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrentImage((currentImage + 3) % images.length);
    }, 4000);

    return () => clearTimeout(timer); // Cleanup on component unmount
  }, [currentImage]);
  return (
    <>
      <div className="hero-section">
        <img src={sliderBg} alt="background" className="hero-background" />
        <div className="center-content">
          {/* Logo and other elements */}
        </div>
        <div className="slide-container">
          {images.slice(currentImage, currentImage + 3).map((image, index) => (
            <div key={index} className={`slide-item ${index === 1 ? 'special-slide' : ''}`}>
              <img src={image} alt={`Slide ${index}`} className="slide-image" />
            </div>
          ))}
        </div>
        <img src={snowpark} alt="Snowpark Decoration" className="snowpark-decor" />
      </div>

      {/* Testimonial Section */}
      <div className="testimonial-section">
        <div className="testimonial-content">
          <img src="url_to_star_image" alt="5star" className="star-image" />
          <p className="testimonial-text">Google 4.9 * Rated Institute</p>
          <h1 className="testimonial-text">SCOPE INDIA, your career partner!</h1>
          <h2 className="testimonial-text">One of the best Training Destination for Software, Networking and Cloud Computing courses in India with 17 years of Industrial experience.</h2>
          <p className="testimonial-text">Software, Networking, and Cloud Professional Education Centre in Kerala from Suffix E Solutions with WORKING PROFESSIONALS oriented on-the-job TRAINING model. SCOPE INDIA provides courses for ,<span className="highlight"> Software Programming </span>  in Python (Data Science | Artificial Intelligence | Machine Learning | Deep Learning, Data Analytics), Java, PHP, .Net, <span className="highlight">Software Testing </span>Manual and Automation,  <span className="highlight">Cloud Computing </span> (AWS | Azure),<span className="highlight"> Server Administration </span>  (MCSE | RHCE), <span className="highlight"> Networking</span>  (CCNA),  <span className="highlight">  Mobile App Development </span> in Flutter, and   <span className="highlight"> Digital Marketing. </span> Training 
     with 100% Trusted Job Based Internship Model. SCOPE INDIA has a Strong Placement Cell that provides jobs to thousands of students every
     year. We assure you, you won't regret it after training from SCOPE INDIA!</p>
        </div>
      </div>

      {/* Navigation */}
      <div className="navigation-footer">
        {/* Navigation content goes here */}
      </div>

      {/* Footer */}
      <div className="navigation-footer">
        {/* Footer content goes here */}
      </div>
    </>
  );
};

export default Home;
