import React, { useState, useEffect } from 'react';

// Images
import sliderBg from './images/slider_bg.jpg'; // Adjusted path as per common practice
import scopelogo from './images/scopeweb.png';
import snowpark from './images/snowpark-home-slide-decoration-bottomm.png';
import star from './images/5star.png';

import devops from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/DEVOPS-SCOPE_INDIA.jpg';
import aws from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-aws-certification-course-(1).jpg';
import cloud from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-azure-certification-course.jpg';
import digitalmarketing from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-digital-marketing-course.jpg';
import dotnet from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-dotnet-core-full-stack-course.jpg';
import flutter from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-flutter-fullstack-course.jpg';
import fullstack from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-full-stack-intermediate-offer-sep2022-2 (1).jpg';
import fees from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-full-stack-offer-apr30-share2024.jpg';
import java from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-java-full-stack-course (1).jpg';
import mern from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-mean-mern-stack-course.jpg';
import network from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-network-server-administration-course (1).jpg';
import php from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-php-full-stack-course (1).jpg';
import python from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-python-full-stack-course (1).jpg';
import software from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-software-testing-manual-automation-course.jpg';
import software2 from './images/Learn Software_ Networking_ Testing and Cloud courses _ Online _ Offline/scope-india-software-testing-manual-automation-course.jpg';
impor
// Components
import ContactForm from '../components/ContactForm';
import Homenavigate from '../components/Homenavigate';
import Footer from '../components/Footer';

const Home = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const images = [devops, aws, cloud, digitalmarketing, dotnet, flutter, fullstack, fees, java, mern, network, php, python, software, software2];

  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrentImage((currentImage + 3) % images.length);
    }, 4000);

    return () => clearTimeout(timer); // Cleanup on component unmount
  }, [currentImage]);

  return (
    <>
      {/* Hero Section */}
      <div style={{ position: 'relative', height: '90rem' }}>
        <img src={sliderBg} alt="background" style={{ position: 'absolute', width: '100%', height: '100%', objectFit: 'cover' }} />
        <div style={{ position: 'absolute', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <img src={scopelogo} alt="logo" style={{ height: '3rem', width: '3rem', paddingBottom: '40rem' }} />
        </div>
        <div style={{ position: 'absolute', display: 'flex', justifyContent: 'center', alignItems: 'center', marginTop: '45rem', gap: '1rem' }}>
          {images.slice(currentImage, currentImage + 3).map((image, index) => (
            <div key={index} style={{ aspectRatio: '1 / 2', width: `${index === 1 ? '31.25rem' : '22.5rem'}`, height: `${index === 1 ? '50rem' : '37.5rem'}`}}>
              <img src={image} alt={`Slide ${index}`} style={{ width: '50rem', height: '45rem', objectFit: 'cover' }} />
            </div>
          ))}
        </div>
        <img src={snowpark} alt="Snowpark Decoration" style={{ width: '100%', height: 'auto', position: 'absolute', justifyContent: 'center', marginTop: '53.125rem' }} />
      </div>

      {/* Contact Form */}
      <div style={{ position: 'relative' }}>
        <div style={{ maxWidth: '32rem', margin: '0 auto' }}>
          <ContactForm />
        </div>
      </div>

      {/* Testimonial Section */}
      <div style={{ position: 'relative', padding: '3rem', height: '40rem' }}>
        <div style={{ maxWidth: '32rem', margin: '0 auto', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
          <img src={star} alt="5star" style={{ height: '6rem', width: 'auto', marginBottom: '1rem' }} />
          <p style={{ color: '#E50914', fontSize: '1.25rem', fontWeight: '600', marginBottom: '1rem' }}>Google 4.9 * Rated Institute</p>
          <h1 style={{ fontSize: '2.5rem', fontWeight: '800', color: '#000', textAlign: 'center', marginBottom: '2rem' }}>SCOPE INDIA, your career partner!</h1>
          <h2 style={{ fontSize: '1.75rem', fontWeight: '800', color: '#000', textAlign: 'center', marginBottom: '2rem' }}>One of the best Training Destination for Software, Networking and Cloud Computing courses in India with 17 years of Industrial experience.</h2>
          <p style={{ fontSize: '1.125rem', fontWeight: 'normal', color: '#000', textAlign: 'center', marginBottom: '2rem' }}>Software, Networking, and Cloud Professional Education Centre in Kerala from Suffix E Solutions with WORKING PROFESSIONALS oriented on-the-job TRAINING model. SCOPE INDIA provides courses for ,<span style={{ fontWeight: 'bold' }}> Software Programming </span>  in Python (Data Science | Artificial Intelligence | Machine Learning | Deep Learning, Data Analytics), Java, PHP, .Net, <span style={{ fontWeight: 'bold' }}>Software Testing </span>Manual and Automation,  <span style={{ fontWeight: 'bold' }}>Cloud Computing </span> (AWS | Azure),<span style={{ fontWeight: 'bold' }}> Server Administration </span>  (MCSE | RHCE), <span style={{ fontWeight: 'bold' }}> Networking</span>  (CCNA),  <span style={{ fontWeight: 'bold' }}>  Mobile App Development </span> in Flutter, and   <span style={{ fontWeight: 'bold' }}> Digital Marketing. </span> Training 
     with 100% Trusted Job Based Internship Model. SCOPE INDIA has a Strong Placement Cell that provides jobs to thousands of students every
     year. We assure you, you won't regret it after training from SCOPE INDIA!  <br/> <br/> </p>
        </div>
      </div>

      {/* Navigation */}
      <div style={{ position: 'relative' }}>
        <div style={{ maxWidth: '32rem', margin: '0 auto' }}>
          <Homenavigate />
        </div>
      </div>

      {/* Footer */}
      <div style={{ position: 'relative' }}>
        <div style={{ maxWidth: '56rem', margin: '0 auto' }}>
          <Footer />
        </div>
      </div>
    </>
  );
};

export default Home;
