import React from 'react';
import { useNavigate } from 'react-router-dom'; // Correct import
// import mountain from '../image/snowpark-skis-headerbg2.jpg';
import star from '../image/5star.png';
import Locations from './Location';
import Footer from './Footer';
import './css/contact.css'

const Contact = () => {
  const [isSubmitted, setIsSubmitted] = React.useState(false);
  const [name, setName] = React.useState('');
  const [phone, setPhone] = React.useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.trim() !== '' && phone.trim() !== '' && /^\d+$/.test(phone)) {
      setIsSubmitted(true);
      setTimeout(() => {
        setIsSubmitted(false);
      }, 3000);
    } else {
      alert('Please fill out the form correctly.');
    }
  };

  const navigate = useNavigate(); // Correct usage

  const goToCoursesPage = () => {
    navigate('/Courses');
  };

  const goToRegisterPage = () => {
    navigate('/Register');
  };
  return (
    <>
    <div className="contact-container">
        {/* <img src={mountain} alt="Mountain Image" className="mountain-image" /> */}
        <div className="header-text">
          <h1 className='headingcont'>SCOPE INDIA is open 365 days a year</h1>
          <h2 className=''>Let's discuss your career, 24/7 free Consultation</h2>
          <img src={star} alt="5 Star" className="star-image" />
          <p>Google 4.9 * Rated Institute</p>
        </div>
        {/* Form Section */}
        <div className="form-section">
          <h2 className="section-title">GET A FREE CALL BACK</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Enter Your Name"
              id="name"
              name="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="input-field"
            />
            <input
              type="tel"
              placeholder="Enter Your Phone Number"
              id="phone"
              name="phone"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="input-field"
            />
            <button type="submit" className="submit-button">CALL ME</button>
          </form>
          {isSubmitted && (
            <div className="success-message">
              THANK YOU WE HAVE RECEIVED YOUR REQUEST
            </div>
          )}
          <div className="action-buttons">
            <button className="recent-placements-button">Recent Placements</button>
            <button className="courses-button" onClick={goToCoursesPage}>Courses</button>
            <button className="register-now-button" onClick={goToRegisterPage}>Register Now!</button>
          </div>
        </div>
     
      <div>
        <Locations />
      </div>
    
    </div>
     <div className="footer-container">
     <Footer />
   </div>
   </>
  );
};

export default Contact;
