import React from 'react';
import HomeNavigate from './Homenavigate'; // Adjust the path as necessary
import Footer from './Footer'; // Adjust the path as necessary
import './css/about.css'
import backgroundImage from '../image/snowpark-skis-headerbg2.jpg'

const About = () => {
  return (
     <div id='about'>
    <div 
    className="background-image"
    style={{
      backgroundImage: `url(${backgroundImage})`,
      backgroundSize: 'cover',
      height: '100p', // Set a specific height or use other methods to control the size
      width: '100%' // Adjust as needed
    }}
  >
     
       
            <div className="content-wrapper">
              <h1 className="heading-one">SCOPE INDIA, your career partner!</h1>
              <h2 className="sub-heading">One of the best Training Destination for Software, Networking and Cloud Computing courses in India with 17 years of Industrial experience.</h2>
              <p className="description">Software, Networking, and Cloud Professional Education Centre in Kerala from Suffix E Solutions with WORKING PROFESSIONALS oriented on-the-job TRAINING model. SCOPE INDIA provides courses for ,<span className='bold-text'> Software Programming </span>  in Python (Data Science | Artificial Intelligence | Machine Learning | Deep Learning, Data Analytics), Java, PHP, .Net, <span className='bold-text'>Software Testing </span>Manual and Automation,  <span className='bold-text'>Cloud Computing </span> (AWS | Azure),<span className='bold-text'> Server Administration </span>  (MCSE | RHCE), <span className='bold-text'> Networking</span>  (CCNA),  <span className='bold-text'>  Mobile App Development </span> in Flutter, and   <span className='bold-text'> Digital Marketing. </span> Training with 100% Trusted Job Based Internship Model. SCOPE INDIA has a Strong Placement Cell that provides jobs to thousands of students every year. We assure you, you won't regret it after training from SCOPE INDIA!  <br/> <br/></p>
              <div className="fivestar"></div>
              <p className="rating-text">Google 4.9 * Rated Institute</p>
              <div className="home-navigate">
                <HomeNavigate />
              </div>
              </div>
          
        
      </div>
      <div className="footer-container">
        <Footer />
      </div>
    </div>
  );
};

export default About;
